import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { FaGithub, FaExternalLinkAlt } from 'react-icons/fa';

const WorkSection = styled.section`
  min-height: 100vh;
  background: white;
  padding: 100px 0;
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
`;

const SectionTitle = styled(motion.h2)`
  font-size: 3rem;
  font-weight: 700;
  color: #333;
  text-align: center;
  margin-bottom: 3rem;

  @media (max-width: 768px) {
    font-size: 2rem;
  }
`;

const ProjectsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
  gap: 2rem;

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
  }
`;

const ProjectCard = styled(motion.div)`
  background: white;
  border-radius: 16px;
  overflow: hidden;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s ease, box-shadow 0.3s ease;

  &:hover {
    transform: translateY(-10px);
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.15);
  }
`;

const ProjectImage = styled.div`
  height: 200px;
  background: linear-gradient(135deg, ${props => props.gradient || '#ff6b6b 0%, #ffa726 100%'});
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-size: 3rem;
  font-weight: 700;
`;

const ProjectContent = styled.div`
  padding: 2rem;

  h3 {
    font-size: 1.5rem;
    font-weight: 600;
    color: #333;
    margin-bottom: 1rem;
  }

  p {
    color: #666;
    line-height: 1.6;
    margin-bottom: 1.5rem;
  }
`;

const TechStack = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
  margin-bottom: 1.5rem;
`;

const TechTag = styled.span`
  background: #f0f0f0;
  color: #666;
  padding: 0.3rem 0.8rem;
  border-radius: 20px;
  font-size: 0.8rem;
  font-weight: 500;
`;

const ProjectLinks = styled.div`
  display: flex;
  gap: 1rem;
`;

const LinkButton = styled.a`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.8rem 1.5rem;
  background: ${props => props.primary ? 'linear-gradient(135deg, #ff6b6b 0%, #ffa726 100%)' : 'transparent'};
  color: ${props => props.primary ? 'white' : '#ff6b6b'};
  text-decoration: none;
  border-radius: 8px;
  font-weight: 500;
  transition: all 0.3s ease;
  border: 2px solid #667eea;

  &:hover {
    transform: translateY(-2px);
    background: ${props => props.primary ? 'linear-gradient(135deg, #5a6fd8 0%, #6a4190 100%)' : '#667eea'};
    color: white;
  }
`;

const Work = () => {
  const projects = [
    {
      id: 1,
      title: "E-Commerce Platform",
      description: "A full-stack e-commerce application built with React frontend, Java Spring Boot backend, and PostgreSQL database. Features include user authentication, product catalog, shopping cart, and payment processing.",
      tech: ["React", "Java", "Spring Boot", "PostgreSQL", "AWS"],
      gradient: "#667eea 0%, #764ba2 100%",
      githubUrl: "https://github.com/yourusername/ecommerce-platform",
      demoUrl: "https://your-demo-url.com"
    },
    {
      id: 2,
      title: "Task Management App",
      description: "A collaborative task management application with real-time updates, team collaboration features, and project tracking capabilities. Built with modern web technologies.",
      tech: ["React", "Node.js", "WebSocket", "MongoDB", "Docker"],
      gradient: "#f093fb 0%, #f5576c 100%",
      githubUrl: "https://github.com/yourusername/task-manager",
      demoUrl: "https://your-demo-url.com"
    },
    {
      id: 3,
      title: "Weather Dashboard",
      description: "A responsive weather dashboard that provides real-time weather data, forecasts, and location-based weather information with interactive charts and maps.",
      tech: ["React", "JavaScript", "Chart.js", "API Integration"],
      gradient: "#4facfe 0%, #00f2fe 100%",
      githubUrl: "https://github.com/yourusername/weather-dashboard",
      demoUrl: "https://your-demo-url.com"
    },
    {
      id: 4,
      title: "Social Media Analytics",
      description: "A comprehensive analytics platform for social media metrics, user engagement tracking, and content performance analysis with data visualization.",
      tech: ["React", "Python", "Django", "PostgreSQL", "AWS"],
      gradient: "#43e97b 0%, #38f9d7 100%",
      githubUrl: "https://github.com/yourusername/social-analytics",
      demoUrl: "https://your-demo-url.com"
    }
  ];

  return (
    <WorkSection id="work">
      <Container>
        <SectionTitle
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          My Work
        </SectionTitle>

        <ProjectsGrid>
          {projects.map((project, index) => (
            <ProjectCard
              key={project.id}
              initial={{ opacity: 0, y: 50 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: index * 0.2 }}
              viewport={{ once: true }}
            >
              <ProjectImage gradient={project.gradient}>
                {project.title.charAt(0)}
              </ProjectImage>
              <ProjectContent>
                <h3>{project.title}</h3>
                <p>{project.description}</p>
                <TechStack>
                  {project.tech.map((tech, techIndex) => (
                    <TechTag key={techIndex}>{tech}</TechTag>
                  ))}
                </TechStack>
                <ProjectLinks>
                  <LinkButton href={project.githubUrl} target="_blank" rel="noopener noreferrer">
                    <FaGithub />
                    Code
                  </LinkButton>
                  <LinkButton primary href={project.demoUrl} target="_blank" rel="noopener noreferrer">
                    <FaExternalLinkAlt />
                    Demo
                  </LinkButton>
                </ProjectLinks>
              </ProjectContent>
            </ProjectCard>
          ))}
        </ProjectsGrid>
      </Container>
    </WorkSection>
  );
};

export default Work;
