import React, { useState } from 'react';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import { FaGithub, FaExternalLinkAlt, FaCode, FaGraduationCap, FaProjectDiagram } from 'react-icons/fa';
import { 
  SiReact, 
  SiJavascript, 
  SiTypescript, 
  SiSpring, 
  SiPostgresql, 
  SiAmazonaws, 
  SiDocker, 
  SiGit,
  SiHtml5,
  SiCss3,
  SiNodedotjs,
  SiPython,
  SiMongodb,
  SiRedis,
  SiKubernetes,
  SiJenkins,
  SiTerraform
} from 'react-icons/si';
import { FaJava } from 'react-icons/fa';

const WorkSection = styled.section`
  min-height: 100vh;
  background: white;
  padding: 100px 0;
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
`;

const SectionTitle = styled(motion.h2)`
  font-size: 3rem;
  font-weight: 700;
  color: #333;
  text-align: center;
  margin-bottom: 3rem;

  @media (max-width: 768px) {
    font-size: 2rem;
  }
`;

const SubHeader = styled.div`
  display: flex;
  justify-content: center;
  margin-bottom: 3rem;
  background: #f8f9fa;
  border-radius: 16px;
  padding: 0.5rem;
  max-width: 1200px;
  margin-left: auto;
  margin-right: auto;

  @media (max-width: 1200px) {
    max-width: 100%;
    margin: 0 20px 3rem 20px;
  }
`;

const SubHeaderItem = styled.button`
  flex: 1;
  padding: 1rem 2rem;
  border: none;
  background: ${props => props.active ? 'white' : 'transparent'};
  color: ${props => props.active ? '#ff6b6b' : '#666'};
  border-radius: 12px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
